    private void jLabelCodificarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelCodificarMouseClicked
        
        FuncoesControle obj = new FuncoesControle();
        String texto, codigo;
        
        texto = jTextAreaReceptor.getText();
        codigo = obj.lerTexto(texto);
        jTextAreaResultado.setText(codigo);

    }//GEN-LAST:event_jLabelCodificarMouseClicked

    private void jLabelDecodificarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelDecodificarMouseClicked
        
        FuncoesControle obj = new FuncoesControle();
        String texto, codigo;
        
        codigo = jTextAreaReceptor.getText();
        texto = obj.lerCodigo(codigo);
        jTextAreaResultado.setText(texto);
        
    }//GEN-LAST:event_jLabelDecodificarMouseClicked

